/*\
module-type: library

This handles all logging and alerts Relink emits.

\*/

var prettylink = require("$:/plugins/flibbles/relink/js/relinkoperations/text/wikitext/prettylink.js");
var Placeholder = require("$:/plugins/flibbles/relink/js/utils/placeholder.js");

exports.eachImpossible = function(rootEntry, method) {
	if (rootEntry.eachChild) {
		rootEntry.eachChild(function(child) {
			exports.eachImpossible.call(this, child, method);
		});
	}
	if (rootEntry.impossible) {
		method(rootEntry);
	}
};

exports.logAll = function(entry, title, from, to, options) {
	var raw = exports.log[entry.name];
	if (entry.impossible) {
		return;
	}
	if (raw) {
		exports.logRelink(raw, entry, title, from, to, options);
		return;
	}
	if (entry.eachChild) {
		entry.eachChild(function(child) {
			exports.logAll(child, title, from, to, options);
		});
	}
};

exports.logRelink = function(raw, args, title, from, to, options) {
	raw = "Renaming '"+from+"' to '"+to+"' in " + raw + " of tiddler '"+title+"'";
	// This is cheap, but whatevs. To do a proper
	// rendering would require working through a wiki
	// object. Too heavy weight for log messages.
	var msg = raw.replace(/<<([^<>]+)>>/g, function(match, key) {
		var value = args[key];
		if (key === "field") {
			value = descriptor(value);
		};
		return value || ("<<"+key+">>");
	});
	console.log(msg);
};

// This wraps alert so it can be monkeypatched during testing.
exports.alert = function(message) {
	alert(message);
};

exports.getString = function(title, options) {
	title = "$:/plugins/flibbles/relink/language/" + title;
	return options.wiki.renderTiddler("text/plain", title, options);
};

var logger;

exports.reportFailures = function(failureList, options) {
	if (!logger) {
		logger = new $tw.utils.Logger("Relinker");
	}
	var alertString = this.getString("Error/ReportFailedRelinks", options)
	var placeholder = new Placeholder(options);
	var phOptions = $tw.utils.extend({placeholder: placeholder}, options);
	var alreadyReported = Object.create(null);
	var reportList = [];
	$tw.utils.each(failureList, function(f) {
		if (!alreadyReported[f]) {
			if ($tw.browser) {
				reportList.push("\n* " + prettylink.makeLink(f, undefined, phOptions));
			} else {
				reportList.push("\n* " + f);
			}
			alreadyReported[f] = true;
		}
	});
	logger.alert(placeholder.getPreamble() + alertString + "\n" + reportList.join(""));
};

exports.log = {
	"html": "<<<element>> /> element",
	"field": "<<field>>",
	"filteredtransclude": "filtered transclusion",
	"image": "image",
	"import": "\\import filter",
	"macrodef": "<<macro>> definition",
	"prettylink": "prettylink",
	"syslink": "syslink",
	"transclude": "transclusion",
	"wikilink": "CamelCase link",
};

function descriptor(field) {
	if (field === "tags") {
		return "tags";
	} else {
		return field + " field" ;
	}
};

$:/plugins/flibbles/relink/js/mangler.js.text:
/*\
module-type: widget

Creates a mangler widget for field validation. This isn't meant to be used
by the user. It's only used in Relink configuration.

\*/

var Widget = require("$:/core/modules/widgets/widget.js").widget;
var language = require('$:/plugins/flibbles/relink/js/language.js');
var settings = require('$:/plugins/flibbles/relink/js/settings.js');

var RelinkManglerWidget = function(parseTreeNode,options) {
	this.initialise(parseTreeNode,options);
	this.addEventListeners([
		{type: "relink-add-field", handler: "handleAddFieldEvent"},
		{type: "relink-add-operator", handler: "handleAddOperatorEvent"},
		{type: "relink-add-parameter", handler: "handleAddParameterEvent"},
		{type: "relink-add-attribute", handler: "handleAddAttributeEvent"}
	]);
};

exports.relinkmangler = RelinkManglerWidget;

RelinkManglerWidget.prototype = new Widget();

RelinkManglerWidget.prototype.handleAddFieldEvent = function(event) {
	var param = event.paramObject;
	if (typeof param !== "object" || !param.field) {
		// Can't handle it.
		return true;
	}
	var trimmedName = param.field.toLowerCase().trim();
	if (!trimmedName) {
		// Still can't handle it, but don't warn.
		return true;
	}
	if(!$tw.utils.isValidFieldName(trimmedName)) {
		language.alert($tw.language.getString(
			"InvalidFieldName",
			{variables:
				{fieldName: trimmedName}
			}
		));
	} else {
		add(this.wiki, "fields", trimmedName);
	}
	return true;
};

/**Not much validation, even though there are definitely illegal
 * operator names. If you input on, Relink won't relink it, but it
 * won't choke on it either. Tiddlywiki will...
 */
RelinkManglerWidget.prototype.handleAddOperatorEvent = function(event) {
	var param = event.paramObject;
	if (param) {
		add(this.wiki, "operators", param.operator);
	}
	return true;
};

RelinkManglerWidget.prototype.handleAddParameterEvent = function(event) {
	var param = event.paramObject;
	if (param && param.macro && param.parameter) {
		if (/\s/.test(param.macro.trim())) {
			language.alert(language.getString(
				"Error/InvalidMacroName",
				{ variables: {macroName: param.macro},
				  wiki: this.wiki
				}
			));
		} else if (/[ \/]/.test(param.parameter.trim())) {
			language.alert(language.getString(
				"Error/InvalidParameterName",
				{ variables: {parameterName: param.parameter},
				  wiki: this.wiki
				}
			));
		} else {
			add(this.wiki, "macros", param.macro, param.parameter);
		}
	}
	return true;
};

RelinkManglerWidget.prototype.handleAddAttributeEvent = function(event) {
	var param = event.paramObject;
	if (param && param.element && param.attribute) {
		if (/[ \/]/.test(param.element.trim())) {
			language.alert(language.getString(
				"Error/InvalidElementName",
				{ variables: {elementName: param.element},
				  wiki: this.wiki
				}
			));
		} else if (/[ \/]/.test(param.attribute.trim())) {
			language.alert(language.getString(
				"Error/InvalidAttributeName",
				{ variables: {attributeName: param.attribute},
				  wiki: this.wiki
				}
			));
		} else {
			add(this.wiki, "attributes", param.element, param.attribute);
		}
	}
	return true;
};

function add(wiki, category/*, path parts*/) {
	var path = "$:/config/flibbles/relink/" + category;
	for (var x = 2; x < arguments.length; x++) {
		var part = arguments[x];
		// Abort if it's falsy, or only whitespace. Also, trim spaces
		if (!part || !(part = part.trim())) {
			return;
		}
		path = path + "/" + part;
	}
	var def = settings.getDefaultType(wiki);
	wiki.addTiddler({title: path, text: def});
};

