/*\
module-type: library

This handles the fetching and distribution of relink settings.

\*/

var fieldTypes = Object.create(null);
var surveyors = [];
var prefix = "$:/config/flibbles/relink/";

$tw.modules.forEachModuleOfType("relinkfieldtype", function(title, exports) {
	function NewType() {};
	NewType.prototype = exports;
	NewType.typeName = exports.name;
	fieldTypes[exports.name] = NewType;
	// For legacy reasons, some of the field types can go by other names
	if (exports.aliases) {
		$tw.utils.each(exports.aliases, function(alias) {
			fieldTypes[alias] = NewType;
		});
	}
});

$tw.modules.forEachModuleOfType("relinksurveyor", function(title, exports) {
	surveyors.push(exports);
});

function Settings(wiki) {
	this.settings = compileSettings(wiki);
	this.wiki = wiki;
};

module.exports = Settings;

/**Returns a specific relinker.
 * This is useful for wikitext rules which need to parse a filter or a list
 */
Settings.getType = function(name) {
	var Handler = fieldTypes[name];
	return Handler ? new Handler() : undefined;
};

Settings.getTypes = function() {
	// We don't return fieldTypes, because we don't want it modified,
	// and we need to filter out legacy names.
	var rtn = Object.create(null);
	for (var type in fieldTypes) {
		var typeObject = fieldTypes[type];
		rtn[typeObject.typeName] = typeObject;
	}
	return rtn;
};

Settings.getDefaultType = function(wiki) {
	var tiddler = wiki.getTiddler("$:/config/flibbles/relink/settings/default-type");
	var defaultType = tiddler && tiddler.fields.text;
	// make sure the default actually exists, otherwise default
	return fieldTypes[defaultType] ? defaultType : "title";
};

Settings.prototype.survey = function(text, fromTitle, options) {
	if (text) {
		for (var i = 0; i < surveyors.length; i++) {
			if (surveyors[i].survey(text, fromTitle, options)) {
				return true;
			}
		}
	}
	return false;
};

Settings.prototype.getAttribute = function(elementName) {
	return this.settings.attributes[elementName];
};

Settings.prototype.getAttributes = function() {
	return flatten(this.settings.attributes);
};


Settings.prototype.getFields = function() {
	return this.settings.fields;
};

Settings.prototype.getOperators = function() {
	return this.settings.operators;
};

Settings.prototype.getMacro = function(macroName) {
	return this.settings.macros[macroName];
};

Settings.prototype.getMacros = function() {
	return flatten(this.settings.macros);
};

Settings.prototype.refresh = function(changes) {
	for (var title in changes) {
		if (title.substr(0, prefix.length) === prefix) {
			this.settings = compileSettings(this.wiki);
			return true;
		}
	}
	return false;
};

/**Factories define methods that create settings given config tiddlers.
 * for factory method 'example', it will be called once for each:
 * "$:/config/flibbles/relink/example/..." tiddler that exists.
 * the argument "key" will be set to the contents of "..."
 *
 * The reason I build relink settings in this convoluted way is to minimize
 * the number of times tiddlywiki has to run through EVERY tiddler looking
 * for relink config tiddlers.
 *
 * Also, by exporting "factories", anyone who extends relink can patch in
 * their own factory methods to create settings that are generated exactly
 * once per rename.
 */
exports.factories = {
	attributes: function(attributes, data, key) {
		var elem = root(key);
		var attr = key.substr(elem.length+1);
		attributes[elem] = attributes[elem] || Object.create(null);
		attributes[elem][attr] = data;
	},
	fields: function(fields, data, name) {
		fields[name] = data;
	},
	macros: function(macros, data, key) {
		// We take the last index, not the first, because macro
		// parameters can't have slashes, but macroNames can.
		var name = dir(key);
		var arg = key.substr(name.length+1);
		macros[name] = macros[name] || Object.create(null);
		macros[name][arg] = data;
	},
	operators: function(operators, data, name) {
		operators[name] = data;
	}
};

function compileSettings(wiki) {
	var settings = Object.create(null);
	for (var name in exports.factories) {
		settings[name] = Object.create(null);
	}
	wiki.eachShadowPlusTiddlers(function(tiddler, title) {
		if (title.substr(0, prefix.length) === prefix) {
			var remainder = title.substr(prefix.length);
			var category = root(remainder);
			var factory = exports.factories[category];
			if (factory) {
				var name = remainder.substr(category.length+1);
				var Handler = fieldTypes[tiddler.fields.text];
				if (Handler) {
					var data = new Handler();
					data.source = title;
					// Secret feature. You can access a config tiddler's
					// fields from inside the fieldtype handler. Cool
					// tricks can be done with this.
					data.fields = tiddler.fields;
					factory(settings[category], data, name);
				}
			}
		}
	});
	return settings;
};

/* Returns first bit of a path. path/to/tiddler -> path
 */
function root(string) {
	var index = string.indexOf('/');
	if (index >= 0) {
		return string.substr(0, index);
	}
};

/* Returns all but the last bit of a path. path/to/tiddler -> path/to
 */
function dir(string) {
	var index = string.lastIndexOf('/');
	if (index >= 0) {
		return string.substr(0, index);
	}
}

function flatten(set) {
	var signatures = Object.create(null);
	for (var outerName in set) {
		var setItem = set[outerName];
		for (var innerName in setItem) {
			signatures[outerName + "/" + innerName] = setItem[innerName];
		}
	}
	return signatures;
};

$:/plugins/flibbles/relink/js/wikimethod.text:
/*\
module-type: wikimethod

Introduces some utility methods used by Relink.

\*/

var MacroSettings = require('$:/plugins/flibbles/relink/js/utils/macroConfig.js');
var Settings = require("$:/plugins/flibbles/relink/js/settings.js");

var relinkOperations = Object.create(null);
$tw.modules.applyMethods('relinkoperator', relinkOperations);

/** Returns a pair like this,
 *  { title: {field: entry, ... }, ... }
 */
exports.getRelinkReport = function(fromTitle, toTitle, options) {
	var cache = this.getGlobalCache("relink-"+fromTitle, function() {
		return Object.create(null);
	});
	if (!cache[toTitle]) {
		cache[toTitle] = getFreshRelinkReport(this, fromTitle, toTitle, options);
	}
	return cache[toTitle];
};

function getFreshRelinkReport(wiki, fromTitle, toTitle, options) {
	options = options || {};
	options.wiki = options.wiki || wiki;
	options.settings = wiki.getRelinkConfig();
	fromTitle = (fromTitle || "").trim();
	toTitle = (toTitle || "").trim();
	var changeList = Object.create(null);
	if(fromTitle && toTitle) {
		var tiddlerList = wiki.getRelinkableTitles();
		for (var i = 0; i < tiddlerList.length; i++) {
			var title = tiddlerList[i];
			var tiddler = wiki.getTiddler(title);
			// Don't touch plugins or JavaScript modules
			if(tiddler
			&& !tiddler.fields["plugin-type"]
			&& tiddler.fields.type !== "application/javascript") {
				try {
					var entries = Object.create(null);
					for (var operation in relinkOperations) {
						relinkOperations[operation](tiddler, fromTitle, toTitle, entries, options);
					}
					for (var field in entries) {
						// So long as there is one key,
						// add it to the change list.
						changeList[title] = entries;
						break;
					}
				} catch (e) {
					// Should we test for instanceof Error instead?: yes
					// Does that work in the testing environment?: no
					if (e.message) {
						e.message = e.message + "\nWhen relinking '" + title + "'";
					}
					throw e;
				}
			}
		}
	}
	return changeList;
};

exports.getRelinkableTitles = function() {
	var toUpdate = "$:/config/flibbles/relink/to-update";
	var self = this;
	return this.getCacheForTiddler(toUpdate, "relink-toUpdate", function() {
		var tiddler = self.getTiddler(toUpdate);
		if (tiddler) {
			return self.compileFilter(tiddler.fields.text);
		} else {
			return self.allTitles;
		}
	})();
};


exports.getRelinkConfig = function() {
	if (this._relinkConfig === undefined) {
		var settings = new Settings(this);
		var config = new MacroSettings(this, settings);
		config.import( "[[$:/core/ui/PageMacros]] [all[shadows+tiddlers]tag[$:/tags/Macro]!has[draft.of]]");
		// All this below is just wiki.addEventListener, only it
		// puts the event in front, because we need to refresh our
		// relink settings before updating tiddlers.
		this.eventListeners = this.eventListeners || {};
		this.eventListeners.change = this.eventListeners.change || [];
		this.eventListeners.change.unshift(function(changes) {
			config.refresh(changes);
		});
		this._relinkConfig = config;
	}
	return this._relinkConfig;
};

$:/plugins/flibbles/relink/js/filteroper.text:
/*\
module-type: allfilteroperator

Filter function for [all[relinkable]].
Returns all tiddlers subject to relinking.

\*/

(function() {

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

exports.relinkable = function(source,prefix,options) {
	return options.wiki.getRelinkableTitles();
};

})();

/*\
module-type: relinkfilteroperator

This filter is meant for internal Relink use only, thus it's
undocumented and subject to change. Also, it's really not great.

Given an input of targets, (possibly just one), outputs all the tiddlers in
which Relink would fail to update <<currentTiddler>> to the operand in ALL
cases.

`[all[tiddlers+system]relink:impossible<toTiddler>]`

\*/

var language = require("$:/plugins/flibbles/relink/js/language.js");

exports.impossible = function(source,operator,options) {
	var from = options.widget && options.widget.getVariable("currentTiddler");
	var to = operator.operand,
		results = [];
	if (from) {
		var records = options.wiki.getRelinkReport(
			from, to, options);
		source(function(tiddler, title) {
			var fields = records[title];
			if (fields) {
				var impossible = false;
				for (var field in fields) {
					language.eachImpossible(fields[field], function() {
						impossible = true;
					});
				}
				if (impossible) {
					results.push(title);
				}
			}
		});
	}
	return results;
};

/*\
module-type: relinkfilteroperator

Given a title as an operand, returns all non-shadow tiddlers that have any
sort of updatable reference to it.


`relink:references[fromTiddler]]`

Returns all tiddlers that reference `fromTiddler` somewhere inside them.

Input is ignored. Maybe it shouldn't do this.
Also, maybe it should properly recon, instead of fake replacing the title with
`__relink_dummy__`
\*/

exports.references = function(source,operator,options) {
	var fromTitle = operator.operand,
		results = [];
	if (fromTitle) {
		var records = options.wiki.getRelinkReport(
			fromTitle, "$:/plugins/flibbles/relink/dummy", options);
		for (var title in records) {
			results.push(title);
		}
	}
	return results;
};

/*\
module-type: filteroperator

This filter acts as a namespace for several small, simple filters, such as

`[relink:impossible[]]`

\*/

/*jslint node: true, browser: true */
/*global $tw: false */
"use strict";

var language = require('$:/plugins/flibbles/relink/js/language.js');

var relinkFilterOperators;

function getRelinkFilterOperators() {
	if(!relinkFilterOperators) {
		relinkFilterOperators = {};
		$tw.modules.applyMethods("relinkfilteroperator",
		                         relinkFilterOperators);
	}
	return relinkFilterOperators;
}

exports.relink = function(source,operator,options) {
	var suffixPair = parseSuffix(operator.suffix);
	var relinkFilterOperator = getRelinkFilterOperators()[suffixPair[0]];
	if (relinkFilterOperator) {
		var newOperator = $tw.utils.extend({}, operator);
		newOperator.suffix = suffixPair[1];
		return relinkFilterOperator(source, newOperator, options);
	} else {
		return [language.getString("Error/RelinkFilterOperator", options)];
	}
};

function parseSuffix(suffix) {
	var index = suffix? suffix.indexOf(":"): -1;
	if (index >= 0) {
		return [suffix.substr(0, index), suffix.substr(index+1)];
	} else {
		return [suffix];
	}
}

/*\
module-type: relinkfilteroperator

Given a title as an operand, returns a string for each occurrence of that title
within each input title.

[[title]] +[relink:report[fromTiddler]]`

Returns string representation of fromTiddler occurrences in title.
\*/

exports.report = function(source,operator,options) {
	var fromTitle = operator.operand,
		results = [],
		records = options.wiki.getRelinkReport(
			fromTitle, fromTitle, options);
	if (fromTitle) {
		source(function(tiddler, title) {
			var affectedFields = records[title];
			if (affectedFields) {
				for (var field in affectedFields) {
					var entry = affectedFields[field];
					var signatures = entry.report();
					results = results.concat(signatures);
				}
			}
		});
	}
	return results;
};

/*\
module-type: relinkfilteroperator

This filter returns all input tiddlers which are a source of
relink configuration.

`[all[tiddlers+system]relink:source[macros]]`

\*/

var settings = require('$:/plugins/flibbles/relink/js/settings.js');

exports.signatures = function(source,operator,options) {
	var plugin = operator.operand || null;
	var set = getSet(options);
	if (plugin === "$:/core") {
		// Core doesn't actually have any settings. We mean Relink
		plugin = "$:/plugins/flibbles/relink";
	}
	var signatures = [];
	for (var signature in set) {
		var source = set[signature].source;
		if (options.wiki.getShadowSource(source) === plugin) {
			signatures.push(signature);
		}
	}
	return signatures;
};

exports.type = function(source,operator,options) {
	var results = [];
	var set = getSet(options);
	source(function(tiddler, signature) {
		if (set[signature]) {
			results.push(set[signature].name);
		}
	});
	return results;
};

exports.types = function(source,operator,options) {
	var def = settings.getDefaultType(options.wiki);
	var types = Object.keys(settings.getTypes());
	types.sort();
	// move default to front
	types.sort(function(x,y) { return x === def ? -1 : y === def ? 1 : 0; });
	return types;
};

exports.source = function(source,operator,options) {
	var results = [];
	var category = operator.suffix;
