/*\

Checks for fromTitle in a tiddler's text. If found, sees if it's relevant,
and tries to swap it out if it is.

\*/

/*jslint node: false, browser: true */
/*global $tw: false */
"use strict";

var type = 'text/vnd.tiddlywiki';
var Placeholder = require("$:/plugins/flibbles/relink/js/utils/placeholder.js");
var settings = require('$:/plugins/flibbles/relink/js/settings.js');
var wikitextHandler = settings.getType('wikitext');

exports[type] = function(tiddler, fromTitle, toTitle, options) {
	var placeholder = new Placeholder();
	var currentOptions = $tw.utils.extend(
		{
			currentTiddler: tiddler.fields.title,
			placeholder: placeholder
		}, options);
	var entry = wikitextHandler.relink(tiddler.fields.text, fromTitle, toTitle, currentOptions);
	if (entry && entry.output) {
		// If there's output, we've also got to prepend any macros
		// that the placeholder defined.
		var preamble = placeholder.getPreamble();
		entry.output = preamble + entry.output;
	}
	return entry;
}

/*\
module-type: relinkwikitextrule

Handles code blocks. Or rather //doesn't// handle them, since we should
ignore their contents.

"`` [[Renamed Title]] ``" will remain unchanged.

\*/

exports.name = ["codeinline", "codeblock"];

exports.relink = function(text, fromTitle, toTitle, options) {
	var reEnd;
	this.parser.pos = this.matchRegExp.lastIndex;
	// I'm lazy. This relink method works for both codeblock and codeinline
	if (this.match[0].length > 2) {
		// Must be a codeblock
		reEnd = /\r?\n```$/mg;
	} else {
		// Must be a codeinline
		reEnd = new RegExp(this.match[1], "mg");
	}
	reEnd.lastIndex = this.parser.pos;
	var match = reEnd.exec(text);
	if (match) {
		this.parser.pos = match.index + match[0].length;
	} else {
		this.parser.pos = this.parser.sourceLength;
	}
	return undefined;
};

/*\
module-type: relinkwikitextrule

Handles comment blocks. Or rather //doesn't// handle them, since we should
ignore their contents.

"<!-- [[Renamed Title]] -->" will remain unchanged.

\*/

exports.name = ["commentinline", "commentblock"];

exports.relink = function(text, fromTitle, toTitle, options) {
	this.parser.pos = this.endMatchRegExp.lastIndex;
	return undefined;
};

/*\
module-type: relinkwikitextrule

Handles replacement of filtered transclusions in wiki text like,

{{{ [tag[docs]] }}}
{{{ [tag[docs]] |tooltip}}}
{{{ [tag[docs]] ||TemplateTitle}}}
{{{ [tag[docs]] |tooltip||TemplateTitle}}}
{{{ [tag[docs]] }}width:40;height:50;}.class.class

This renames both the list and the template field.

\*/

exports.name = ['filteredtranscludeinline', 'filteredtranscludeblock'];

var filterHandler = require("$:/plugins/flibbles/relink/js/settings").getType('filter');
var utils = require("./utils.js");
var EntryNode = require('$:/plugins/flibbles/relink/js/utils/entry');

var FilteredTranscludeEntry = EntryNode.newType("filteredtransclude");

FilteredTranscludeEntry.prototype.report = function() {
	var output = [];
	var self = this;
	$tw.utils.each(this.children, function(child) {
		if (child.name === "filter") {
			var append = "}}}";
			if (self.template) {
				append = "||" + self.template + append;
			}
			$tw.utils.each(child.report(), function(report) {
				output.push("{{{" + report + append);
			});
		} else {
			// Must be the template
			output.push("{{{" + self.filter + "||}}}");
		}
	});
	return output;
};

exports.relink = function(text, fromTitle, toTitle, options) {
	var m = this.match;
		filter = m[1],
		tooltip = m[2],
		template = m[3],
		style = m[4],
		classes = m[5],
		parser = this.parser,
		entry = new FilteredTranscludeEntry();
	parser.pos = this.matchRegExp.lastIndex;
	var modified = false;

	var filterEntry = filterHandler.relink(filter, fromTitle, toTitle, options);
	if (filterEntry !== undefined) {
		entry.add(filterEntry);
		if (filterEntry.output) {
			modified = true;
			filter = filterEntry.output;
		}
	}

	if ($tw.utils.trim(template) === fromTitle) {
		// preserves user-inputted whitespace
		template = template.replace(fromTitle, toTitle);
		entry.add({name: "title", output: template});
		modified = true;
	}
	if (!modified) {
		if (entry.children.length <= 0) {
			return undefined;
		}
	} else {
		var output = this.makeFilteredtransclude(filter, tooltip, template, style, classes, options);
		if (output === undefined) {
			entry.impossible = true;
		} else {
			// By copying over the ending newline of the original
			// text if present, thisrelink method thus works for
			// both the inline and block rule
			entry.output = output + utils.getEndingNewline(m[0]);
		}
		entry.filter = filter;
	}
	entry.template = template;
	return entry;
};

exports.makeFilteredtransclude = function(filter, tooltip, template, style, classes, options) {
	if (canBePretty(filter) && canBePrettyTemplate(template)) {
		return prettyList(filter, tooltip, template, style, classes);
	} else if (!options.noWidgets) {
		return widget(filter, tooltip, template, style, classes, options);
	}
	return undefined;
};

function prettyList(filter, tooltip, template, style, classes) {
	if (tooltip === undefined) {
		tooltip = '';
	} else {
		tooltip = "|" + tooltip;
	}
	if (template === undefined) {
		template = '';
	} else {
		template = "||" + template;
	}
	if (classes === undefined) {
		classes = '';
	} else {
		classes = "." + classes;
	}
	style = style || '';
	return "{{{"+filter+tooltip+template+"}}"+style+"}"+classes;
};

/** Returns a filtered transclude as a string of a widget.
 */
function widget(filter, tooltip, template, style, classes, options) {
	var cannotDo = false;
	if (classes !== undefined) {
		classes = classes.split('.').join(' ');
	}
	function wrap(name, value, treatAsTitle) {
		if (!value) {
			return '';
		}
		var wrappedValue = utils.wrapAttributeValue(value);
		if (wrappedValue === undefined) {
			if (!options.placeholder) {
				cannotDo = true;
				return undefined;
			}
			var category = treatAsTitle ? undefined : name;
			wrappedValue = "<<"+options.placeholder.getPlaceholderFor(value,category,options)+">>";
		}
		return " "+name+"="+wrappedValue;
	};
	var widget = [
		"<$list",
		wrap("filter", filter),
		wrap("tooltip", tooltip),
		wrap("template", template, true),
		wrap("style", style),
		wrap("itemClass", classes),
		"/>"
	];
	if (cannotDo) {
		return undefined;
	}
	return widget.join('');
};

function canBePretty(filter) {
	return filter.indexOf('|') < 0 && filter.indexOf('}}') < 0;
};

function canBePrettyTemplate(template) {
	return !template || (
		template.indexOf('|') < 0
		&& template.indexOf('{') < 0
		&& template.indexOf('}') < 0);
};

/*\
module-type: relinkwikitextrule

Handles replacement in attributes of widgets and html elements
This is configurable to select exactly which attributes of which elements
should be changed.

<$link to="TiddlerTitle" />

\*/

var utils = require("./utils.js");
var Rebuilder = require("$:/plugins/flibbles/relink/js/utils/rebuilder");
var settings = require('$:/plugins/flibbles/relink/js/settings.js');
var refHandler = settings.getType('reference');
var filterHandler = settings.getType('filter');
var macrocall = require("./macrocall.js");
var EntryNode = require('$:/plugins/flibbles/relink/js/utils/entry');

exports.name = "html";

var HtmlEntry = EntryNode.newCollection("html");

HtmlEntry.prototype.forEachChildReport = function(report, attribute, type) {
	var rtn = attribute;
	if (type === "filtered") {
		rtn += "={{{" + report + "}}}";
	} else if (type === "indirect") {
		rtn += "={{" + report + "}}";
	} else if (type === "macro") {
		rtn += "="+report;
	} else{
		// must be string.
		if (report.length > 0) {
			rtn += '="' + report + '"';
		}
	}
	return "<" + this.element + " " + rtn + " />";
};

exports.relink = function(text, fromTitle, toTitle, options) {
	var managedElement = options.settings.getAttribute(this.nextTag.tag),
		builder = new Rebuilder(text, this.nextTag.start);
	var importFilterAttr;
	var widgetEntry = new HtmlEntry();
	widgetEntry.attributes = Object.create(null);
	widgetEntry.element = this.nextTag.tag;
	for (var attributeName in this.nextTag.attributes) {
		var attr = this.nextTag.attributes[attributeName];
		var nextEql = text.indexOf('=', attr.start);
		// This is the rare case of changing tiddler
		// "true" to something else when "true" is
		// implicit, like <$link to /> We ignore those.
		if (nextEql < 0 || nextEql > attr.end) {
			continue;
		}
		if (this.nextTag.tag === "$importvariables" && attributeName === "filter") {
			importFilterAttr = attr;
		}
		var oldLength, quotedValue = undefined, entry;
		if (attr.type === "string") {
			var handler = getAttributeHandler(this.nextTag, attributeName, options);
			if (!handler) {
				// We don't manage this attribute. Bye.
				continue;
			}
			entry = handler.relink(attr.value, fromTitle, toTitle, options);
			if (entry === undefined) {
				continue;
			}
			if (entry.output) {
				var quote = utils.determineQuote(text, attr);
				oldLength = attr.value.length + (quote.length * 2);
				quotedValue = utils.wrapAttributeValue(entry.output,quote);
				if (quotedValue === undefined) {
					// The value was unquotable. We need to make
					// a macro in order to replace it.
					if (!options.placeholder) {
						// but we can't...
						entry.impossible = true;
					} else {
						var value = options.placeholder.getPlaceholderFor(entry.output,handler.name,options)
						quotedValue = "<<"+value+">>";
					}
				}
			}
		} else if (attr.type === "indirect") {
			entry = refHandler.relinkInBraces(attr.textReference, fromTitle, toTitle, options);
			if (entry === undefined) {
				continue;
			}
			if (entry.output) {
				// +4 for '{{' and '}}'
				oldLength = attr.textReference.length + 4;
				quotedValue = "{{"+entry.output+"}}";
			}
		} else if (attr.type === "filtered") {
			entry = filterHandler.relinkInBraces(attr.filter, fromTitle, toTitle, options);
			if (entry === undefined) {
				continue;
			}
			if (entry.output) {
				// +6 for '{{{' and '}}}'
				oldLength = attr.filter.length + 6;
				quotedValue = "{{{"+ entry.output +"}}}";
			}
		} else if (attr.type === "macro") {
			var macro = attr.value;
			entry = macrocall.relinkAttribute(macro, text, fromTitle, toTitle, options);
			if (entry === undefined) {
				continue;
			}
			if (!entry.impossible) {
				// already includes '<<' and '>>'
				oldLength = macro.end-macro.start;
				quotedValue = entry.output;
			}
		}
		widgetEntry.addChild(entry, attributeName, attr.type);
		if (quotedValue === undefined) {
			continue;
		}
		if (this.nextTag.tag === "$importvariables" && attributeName === "filter") {
			// If this is an import variable filter, we gotta
			// remember this new value when we import lower down.
			importFilterAttr = quotedValue;
		}
		// We count backwards from the end to preserve whitespace
		var valueStart = attr.end - oldLength;
		builder.add(quotedValue, valueStart, attr.end);
	}
	if (importFilterAttr) {
		processImportFilter(importFilterAttr, options);
	}
	this.parser.pos = this.nextTag.end;
	if (widgetEntry.hasChildren()) {
		widgetEntry.output = builder.results(this.nextTag.end);
		return widgetEntry;
	}
	return undefined;
};

/** Returns the field handler for the given attribute of the given widget.
 *  If this returns undefined, it means we don't handle it. So skip.
 */
function getAttributeHandler(widget, attributeName, options) {
	if (widget.tag === "$macrocall") {
		var nameAttr = widget.attributes["$name"];
		if (nameAttr) {
			var macro = options.settings.getMacro(nameAttr.value);
			if (macro) {
				return macro[attributeName];
			}
		}
	} else {
		var element = options.settings.getAttribute(widget.tag);
		if (element) {
			return element[attributeName];
		}
	}
	return undefined;
};

function computeAttribute(attribute, options) {
	var value;
	if(attribute.type === "filtered") {
		var parentWidget = options.settings.getVariableWidget();
		value = options.wiki.filterTiddlers(attribute.filter,parentWidget)[0] || "";
	} else if(attribute.type === "indirect") {
		var parentWidget = options.settings.getVariableWidget();
		value = options.wiki.getTextReference(attribute.textReference,"",parentWidget.variables.currentTiddler.value);
	} else if(attribute.type === "macro") {
		var parentWidget = options.settings.getVariableWidget();
		value = parentWidget.getVariable(attribute.value.name,{params: attribute.value.params});
	} else { // String attribute
		value = attribute.value;
	}
	return value;
};

// This processes a <$importvariables> filter attribute and adds any new
// variables to our parser.
function processImportFilter(importAttribute, options) {
	if (typeof importAttribute === "string") {
		// It was changed. Reparse it. It'll be a quoted
		// attribute value. Add a dummy attribute name.
		importAttribute = $tw.utils.parseAttribute("p="+importAttribute, 0)
	}
	var importFilter = computeAttribute(importAttribute, options);
	options.settings.import(importFilter);
};

/*\
module-type: relinkwikitextrule

Handles replacement in wiki text inline rules, like,

[img[tiddler.jpg]]

[img width=23 height=24 [Description|tiddler.jpg]]

\*/

var Rebuilder = require("$:/plugins/flibbles/relink/js/utils/rebuilder");
var refHandler = require("$:/plugins/flibbles/relink/js/fieldtypes/reference");
var filterHandler = require("$:/plugins/flibbles/relink/js/settings").getType('filter');
var macrocall = require("./macrocall.js");
var utils = require("./utils.js");
var EntryNode = require('$:/plugins/flibbles/relink/js/utils/entry');

exports.name = "image";

var ImageEntry = EntryNode.newCollection("image");

ImageEntry.prototype.forEachChildReport = function(report, attribute, type) {
	var value;
	if (attribute === "source") {
		if (this.tooltip) {
			value = "[img[" + this.tooltip.value + "]]";
		} else {
			value = "[img[]]";
		}
	} else {
		if (type === "indirect") {
			value = "{{" + report + "}}";
		} else if (type === "filtered") {
			value = "{{{" + report + "}}}";
		} else if (type === "macro") {
			// angle brackets already added...
			value = report;
		}
		value = "[img " + attribute + "="+ value + "]";
	}
	return value;
};

exports.relink = function(text, fromTitle, toTitle, options) {
	var ptr = this.nextImage.start;
	var builder = new Rebuilder(text, ptr);
	var makeWidget = false;
	var skipSource = false;
	var imageEntry = new ImageEntry();
	imageEntry.attributes = Object.create(null);
	if (this.nextImage.attributes.source.value === fromTitle && !canBePretty(toTitle, this.nextImage.attributes.tooltip)) {
		if (!options.noWidgets && (utils.wrapAttributeValue(toTitle) || options.placeholder)) {
			makeWidget = true;
			builder.add("<$image", ptr, ptr+4);
		} else {
			// We won't be able to make a placeholder to replace
			// the source attribute. We check now so we don't
			// prematurely convert into a widget.
			// Keep going in case other attributes need replacing.
			skipSource = true;
		}
	}
	ptr += 4; //[img
	var inSource = false;
	for (var attributeName in this.nextImage.attributes) {
		var attr = this.nextImage.attributes[attributeName];
		if (attributeName === "source" || attributeName === "tooltip") {
			if (inSource) {
				ptr = text.indexOf('|', ptr);
			} else {
				ptr = text.indexOf('[', ptr);
				inSource = true;
			}
			if (makeWidget) {
				if (" \t\n".indexOf(text[ptr-1]) >= 0) {
					builder.add('', ptr, ptr+1);
				} else {
					builder.add(' ', ptr, ptr+1);
				}
			}
			ptr += 1;
		}
		if (attributeName === "source") {
			ptr = text.indexOf(attr.value, ptr);
			if (attr.value === fromTitle) {
				var entry = {name: "title"};
				if (makeWidget) {
					var quotedValue = utils.wrapAttributeValue(toTitle);
					if (quotedValue === undefined) {
						var key = options.placeholder.getPlaceholderFor(toTitle, undefined, options);
						builder.add("source=<<"+key+">>", ptr, ptr+fromTitle.length);
					} else {
						builder.add("source="+quotedValue, ptr, ptr+fromTitle.length);
					}
				} else if (!skipSource) {
					builder.add(toTitle, ptr, ptr+fromTitle.length);
				} else {
					entry.impossible = true;
				}
				imageEntry.addChild(entry, attributeName, "string");
			}
			ptr = text.indexOf(']]', ptr);
			if (makeWidget) {
				builder.add("/>", ptr, ptr+2);
			}
			ptr += 2;
		} else if (attributeName === "tooltip") {
			if (makeWidget) {
				ptr = text.indexOf(attr.value, ptr);
				var quotedValue = utils.wrapAttributeValue(attr.value);
				builder.add("tooltip="+quotedValue, ptr, ptr+attr.value.length);
			}
			imageEntry.tooltip = this.nextImage.attributes.tooltip;
		} else {
			ptr = relinkAttribute(attr, builder, fromTitle, toTitle, imageEntry, options);
		}
	}
	this.parser.pos = ptr;
	if (imageEntry.hasChildren()) {
		imageEntry.output = builder.results(ptr);
		return imageEntry;
	}
	return undefined;
};

function relinkAttribute(attribute, builder, fromTitle, toTitle, entry, options) {
	var text = builder.text;
	var ptr = text.indexOf(attribute.name, attribute.start);
	var end;
	ptr += attribute.name.length;
	ptr = text.indexOf('=', ptr);
	if (attribute.type === "string") {
		ptr = text.indexOf(attribute.value, ptr)
		var quote = utils.determineQuote(text, attribute);
		// ignore first quote. We already passed it
		end = ptr + quote.length + attribute.value.length;
	} else if (attribute.type === "indirect") {
		ptr = text.indexOf('{{', ptr);
		var end = ptr + attribute.textReference.length + 4;
		var ref = refHandler.relinkInBraces(attribute.textReference, fromTitle, toTitle, options);
		if (ref) {
			entry.addChild(ref, attribute.name, "indirect");
			if (ref.output) {
				builder.add("{{"+ref.output+"}}", ptr, end);
			}
		}
	} else if (attribute.type === "filtered") {
		ptr = text.indexOf('{{{', ptr);
		var end = ptr + attribute.filter.length + 6;
		var filter = filterHandler.relinkInBraces(attribute.filter, fromTitle, toTitle, options);
		if (filter !== undefined) {
			entry.addChild(filter, attribute.name, "filtered");
			if (filter.output) {
				var quoted = "{{{"+filter.output+"}}}";
				builder.add(quoted, ptr, end);
			}
		}
	} else if (attribute.type === "macro") {
		ptr = text.indexOf("<<", ptr);
		var end = attribute.value.end;
		var macro = attribute.value;
		oldValue = attribute.value;
		var macroEntry = macrocall.relinkAttribute(macro, text, fromTitle, toTitle, options);
		if (macroEntry !== undefined) {
			entry.addChild(macroEntry, attribute.name, "macro");
			if (macroEntry.output) {
				builder.add(macroEntry.output, ptr, end);
			}
		}
	}
	return end;
};

function canBePretty(title, tooltip) {
	return title.indexOf(']') < 0 && (tooltip || title.indexOf('|') < 0);
};

/*\
module-type: relinkwikitextrule

Handles import pragmas

\import [tag[MyTiddler]]
\*/

var settings = require("$:/plugins/flibbles/relink/js/settings.js");
var filterRelinker = settings.getType('filter');

exports.name = "import";

function ImportEntry(filterEntry) {
	this.filter = filterEntry;
};
ImportEntry.prototype.name = "import";
ImportEntry.prototype.eachChild = function(block) { return block(this.filter);};
ImportEntry.prototype.report = function() {
	return this.filter.report().map(function(report) {
		if (report.length > 0) {
			return "\\import " + report;
		} else {
			return "\\import";
		}
	});
};

exports.relink = function(text, fromTitle, toTitle, options) {
	// In this one case, I'll let the parser parse out the filter and move
	// the ptr.
	var start = this.matchRegExp.lastIndex;
	var parseTree = this.parse();
	var filter = parseTree[0].attributes.filter.value;
	var entry = undefined;
	var filterEntry = filterRelinker.relink(filter, fromTitle, toTitle, options);
	if (filterEntry !== undefined) {
		entry = new ImportEntry(filterEntry);
		var newline = text.substring(start+filter.length, this.parser.pos);
		if (filterEntry.output) {
			filter = filterEntry.output;
			entry.output = "\\import " + filter + newline;
		}
	}

	// Before we go, we need to actually import the variables
	// it's calling for, and any /relink pragma
	options.settings.import(filter);

	return entry;
};

/*\
module-type: relinkwikitextrule

Handles macro calls.

<<myMacro '[[MyFilter]]' 'myTitle'>>

\*/

var utils = require("./utils.js");
var Rebuilder = require("$:/plugins/flibbles/relink/js/utils/rebuilder");
var settings = require('$:/plugins/flibbles/relink/js/settings.js');
var EntryNode = require('$:/plugins/flibbles/relink/js/utils/entry');

exports.name = ["macrocallinline", "macrocallblock"];

// Error thrown when a macro's definition is needed, but can't be found.
function CannotFindMacroDef() {};
CannotFindMacroDef.prototype.impossible = true;
CannotFindMacroDef.prototype.name = "macroparam";
// Failed relinks due to missing definitions aren't reported for now.
// I may want to do something special later on.
CannotFindMacroDef.prototype.report = function() { return []; };

var MacrocallEntry = EntryNode.newCollection("macrocall");

MacrocallEntry.prototype.forEachChildReport = function(report, parameter, type) {
	var rtn;
	if (report.length > 0) {
