		} catch (e) {
			if (e instanceof CannotFindMacroDef) {
				macroEntry.addChild(e);
				continue;
			}
		}
		if (index < 0) {
			// this arg either was not supplied, or we can't find
			// the definition, so we can't tie it to an anonymous
			// argument. Either way, move on to the next.
			continue;
		}
