				if (reference.title) {
					var resultTitle = wrap(reference.title, options);
					var attrs = transcludeAttributes(reference.field, reference.index, options);
					if (resultTitle !== undefined && attrs !== undefined) {
						rtn = "<$tiddler tiddler="+resultTitle+"><$transclude tiddler="+resultTemplate+attrs+"/></$tiddler>";
					}
				} else {
					rtn = "<$transclude tiddler="+resultTemplate+"/>";
				}
			}
		}
	} else if (!canBePrettyTitle(reference.title)) {
		if (!options.noWidgets) {
			// This block and the next account for the 1%...
			var resultTitle = wrap(reference.title, options);
			if (resultTitle !== undefined) {
				var reducedRef = {field: reference.field, index: reference.index};
				rtn = "<$tiddler tiddler="+resultTitle+">"+prettyTransclude(reducedRef, template)+"</$tiddler>";
			}
		}
	} else {
		// This block takes care of 99% of all cases
		rtn = prettyTransclude(reference, template);
	}
	return rtn;
};

function wrap(tiddler, options) {
	tiddler = $tw.utils.trim(tiddler);
	var result = utils.wrapAttributeValue(tiddler);
	if (result === undefined) {
		if (options.placeholder) {
			result = "<<" + options.placeholder.getPlaceholderFor(tiddler, undefined, options) + ">>";
		}
	}
	return result;
};

function canBePrettyTitle(value) {
	return refHandler.canBePretty(value) && canBePrettyTemplate(value);
};

function canBePrettyTemplate(value) {
	return !value || (value.indexOf('}') < 0 && value.indexOf('{') < 0 && value.indexOf('|') < 0);
};

/**Returns attributes for a transclude widget.
 * only field or index should be used, not both, but both will return
 * the intuitive (albeit useless) result.
 */
function transcludeAttributes(field, index, options) {
	var rtn = [
		wrapAttribute("field", field, options),
		wrapAttribute("index", index, options)
	];
	if (rtn[0] === undefined || rtn[1] === undefined) {
		// This can only happen if the transclude is using an
		// illegal key.
		return undefined;
	}
	return rtn.join('');
};

function wrapAttribute(name, value, options) {
	if (value) {
		var wrappedValue = utils.wrapAttributeValue(value);
		if (wrappedValue === undefined) {
			if (!options.placeholder) {
				return undefined;
			}
			wrappedValue = "<<"+options.placeholder.getPlaceholderFor(value, name, options)+">>";
		}
		return " "+name+"="+wrappedValue;
	}
	return '';
};

function prettyTransclude(textReference, template) {
	if (typeof textReference !== "string") {
		textReference = refHandler.toString(textReference);
	}
	if (!textReference) {
		textReference = '';
	}
	if (template !== undefined) {
		return "{{"+textReference+"||"+template+"}}";
	} else {
		return "{{"+textReference+"}}";
	}
};

/*\
module-type: library

Utility methods for the wikitext relink rules.

\*/

/**Finds an appropriate quote mark for a given value.
 *
 *Tiddlywiki doesn't have escape characters for attribute values. Instead,
 * we just have to find the type of quotes that'll work for the given title.
 * There exist titles that simply can't be quoted.
 * If it can stick with the preference, it will.
 *
 * return: Returns the wrapped value, or undefined if it's impossible to wrap
 */
exports.wrapAttributeValue = function(value, preference) {
	var whitelist = ["", "'", '"', '"""'];
	var choices = {
		"": function(v) {return !/([\/\s<>"'=])/.test(v); },
		"'": function(v) {return v.indexOf("'") < 0; },
		'"': function(v) {return v.indexOf('"') < 0; },
		'"""': function(v) {return v.indexOf('"""') < 0 && v[v.length-1] != '"';}
	};
	if (choices[preference] && choices[preference](value)) {
		return wrap(value, preference);
	}
	for (var i = 0; i < whitelist.length; i++) {
		var quote = whitelist[i];
		if (choices[quote](value)) {
			return wrap(value, quote);
		}
	}
	// No quotes will work on this
	return undefined;
};

/**Like wrapAttribute value, except for macro parameters, not attributes.
 *
 * These are more permissive. Allows brackets,
 * and slashes and '<' in unquoted values.
 */
exports.wrapParameterValue = function(value, preference) {
	var whitelist = ["", "'", '"', '[[', '"""'];
	var choices = {
		"": function(v) {return !/([\s>"'=])/.test(v); },
		"'": function(v) {return v.indexOf("'") < 0; },
		'"': function(v) {return v.indexOf('"') < 0; },
		"[[": exports.canBePrettyOperand,
		'"""': function(v) {return v.indexOf('"""') < 0 && v[v.length-1] != '"';}
	};
	if (choices[preference] && choices[preference](value)) {
		return wrap(value, preference);
	}
	for (var i = 0; i < whitelist.length; i++) {
		var quote = whitelist[i];
		if (choices[quote](value)) {
			return wrap(value, quote);
		}
	}
	// No quotes will work on this
	return undefined;
};

function wrap(value, wrapper) {
	var wrappers = {
		"": function(v) {return v; },
		"'": function(v) {return "'"+v+"'"; },
		'"': function(v) {return '"'+v+'"'; },
		'"""': function(v) {return '"""'+v+'"""'; },
		"[[": function(v) {return "[["+v+"]]"; }
	};
	var chosen = wrappers[wrapper];
	if (chosen) {
		return chosen(value);
	} else {
		return undefined;
	}
};

exports.canBePrettyOperand = function(value) {
	return value.indexOf(']') < 0;
};

/**Given some text, and a param or  attribute within that text, this returns
 * what type of quotation that attribute is using.
 *
 * param: An object in the form {end:, ...}
 */
exports.determineQuote = function(text, param) {
	var pos = param.end-1;
	if (text[pos] === "'") {
		return "'";
	}
	if (text[pos] === '"') {
		if (text.substr(pos-2, 3) === '"""') {
			return '"""';
		} else {
			return '"';
		}
	}
	if (text.substr(pos-1,2) === ']]' && text.substr((pos-param.value.length)-3, 2) === '[[') {
		return "[[";
	}
	return '';
};

// Finds the newline at the end of a string and returns it. Empty string if
// none exists.
exports.getEndingNewline = function(string) {
	var l = string.length;
	if (string[l-1] === '\n') {
		return (string[l-2] === '\r') ? "\r\n" : "\n";
	}
	return "";
};

/*\
module-type: relinkwikitextrule

Handles CamelCase links

WikiLink

but not:

~WikiLink

\*/

var utils = require("./utils.js");
var prettylink = require('$:/plugins/flibbles/relink/js/relinkoperations/text/wikitext/prettylink.js');

exports.name = "wikilink";

function WikilinkEntry() {};
WikilinkEntry.prototype.name = "wikilink";
WikilinkEntry.prototype.report = function() {
	return [$tw.config.textPrimitives.unWikiLink + this.link];
};

exports.relink = function(text, fromTitle, toTitle, options) {
	var entry = undefined;
	this.parser.pos = this.matchRegExp.lastIndex;
	if (this.match[0] === fromTitle && this.match[0][0] !== $tw.config.textPrimitives.unWikiLink) {
		entry = new WikilinkEntry();
		entry.link = fromTitle;
		entry.output = this.makeWikilink(toTitle, options);
		if (entry.output === undefined) {
			entry.impossible = true;
		}
	}
	return entry;
};

exports.makeWikilink = function(title, options) {
	if (title.match(this.matchRegExp) && title[0] !== $tw.config.textPrimitives.unWikiLink) {
		return title;
	} else {
		return prettylink.makeLink(title, undefined, options);
	}
};

$:/plugins/flibbles/relink/js/surveyors/.text:
/*\

This looks at text and sees if fromTitle is inside of it. That's all.

SURVEYORS

Surveyors are an optimization. They are way of quick-discarding text so it
doesn't have to be interpreted by the wikitext parser, the filter parser,
etc...

The reason I split this off into a module type is in case anyone wants to
relink patterns which might NOT contain the fromTitle in raw text.

They return false for "no", and true for "maybe". If any surveyor returns
"maybe", the text in question is fully parsed.

See the documentation for more details.

\*/

exports.survey = function(text, fromTitle, options) {
	return text.indexOf(fromTitle) >= 0;
};

